		where  CO_ID	=	#{coId,jdbcType=VARCHAR}  
		  and  CNTN_ID	=	#{cntnId,jdbcType=VARCHAR}
		  and  DCMT_RGSR_NO	=	#{dcmtRgsrNo,jdbcType=VARCHAR} 
		  and  POST_INDC LIKE #{postIndc,jdbcType=VARCHAR}

